mapper.select:
SELECT
-- 级联表的字段 ,tushu.tushu_uuid_number as tushuUuidNumber ,tushu.tushu_name as tushuName ,tushu.tushu_photo as tushuPhoto ,tushu.shujia_types as shujiaTypes ,tushu.tushu_types as tushuTypes ,tushu.tushu_zuozhe as tushuZuozhe ,tushu.tushu_chubanshe as tushuChubanshe ,tushu.tushu_kucun_number as tushuKucunNumber ,tushu.shangxia_types as shangxiaTypes ,tushu.tushu_delete as tushuDelete ,tushu.tushu_content as tushuContent ,duzhe.duzhe_uuid_number as duzheUuidNumber ,duzhe.duzhe_name as duzheName ,duzhe.duzhe_phone as duzhePhone ,duzhe.duzhe_id_number as duzheIdNumber ,duzhe.duzhe_photo as duzhePhoto ,duzhe.duzhe_types as duzheTypes ,duzhe.duzhe_email as duzheEmail
FROM tushu_collection a left JOIN tushu tushu ON a.tushu_id = tushu.id left JOIN duzhe duzhe ON a.duzhe_id = duzhe.id
and a.id in
#{item}
and ( a.tushu_id = #{params.tushuId} )
and ( a.duzhe_id = #{params.duzheId} )
and a.tushu_collection_types = #{params.tushuCollectionTypes}
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and a.tushu_id IS NOT NULL
and tushu.tushu_uuid_number like CONCAT('%',#{params.tushuUuidNumber},'%')
and tushu.tushu_name like CONCAT('%',#{params.tushuName},'%')
and tushu.shujia_types = #{params.shujiaTypes}
and tushu.tushu_types = #{params.tushuTypes}
and tushu.tushu_zuozhe like CONCAT('%',#{params.tushuZuozhe},'%')
and tushu.tushu_chubanshe like CONCAT('%',#{params.tushuChubanshe},'%')
and tushu.tushu_kucun_number >= #{params.tushuKucunNumberStart}
and tushu.tushu_kucun_number <= #{params.tushuKucunNumberEnd}
and tushu.tushu_kucun_number = #{params.tushuKucunNumber}
and tushu.shangxia_types = #{params.shangxiaTypes}
and tushu.tushu_delete >= #{params.tushuDeleteStart}
and tushu.tushu_delete <= #{params.tushuDeleteEnd}
and tushu.tushu_delete = #{params.tushuDelete}
and tushu.tushu_content like CONCAT('%',#{params.tushuContent},'%')
and a.duzhe_id IS NOT NULL
and duzhe.duzhe_uuid_number like CONCAT('%',#{params.duzheUuidNumber},'%')
and duzhe.duzhe_name like CONCAT('%',#{params.duzheName},'%')
and duzhe.duzhe_phone like CONCAT('%',#{params.duzhePhone},'%')
and duzhe.duzhe_id_number like CONCAT('%',#{params.duzheIdNumber},'%')
and duzhe.duzhe_types = #{params.duzheTypes}
and duzhe.duzhe_email like CONCAT('%',#{params.duzheEmail},'%')
order by a.${params.orderBy} desc
